points.callback:
if (context.Control.ny != context.y + context.height / 10) context.Control.y = context.y + context.height / 10;
if (context.Control.nx < context.x) context.Control.x = context.x;
if (context.Control.nx > context.x + context.width) context.Control.x = context.x + context.width;

path:
let a = {x: context.x, y: context.y + context.height};
let b = {x: context.x + context.width, y: context.y + context.height};
let c = {x: context.Control.x, y: context.Control.y};
context.path = "M " + a.x + " " + a.y + " L " + b.x + " " + b.y + " L " + c.x + " " + c.y + " Z";
